import React from 'react';
import { IonButton } from '@ionic/react';
import { BluetoothSerial } from '@ionic-native/bluetooth-serial';

let bluetoothComponent = {
  macAddress: 'AA:BB:CC:DD:EE:FF', // dummy MAC address, need to manually enter MAC address of Raspberry Pi

  init: function() {
    this.bindEvents()
  },

  bindEvents: function() {
    document.addEventListener('findDevice', this.connectBluetooth, false)
  },

  connectBluetooth: function() {
    BluetoothSerial.connect(this.macAddress)
    if(BluetoothSerial.isEnabled()) {
      this.onConnect()
    }
    else {
      this.onDisconnect()
    }
  },

  onConnect: function() {
    BluetoothSerial.subscribe('\n')
    let data:string = 'hello world'
    this.sendData(data)
  },

  onDisconnect: function() {
    alert("Device has been disconnected")
  },

  sendData: function(data:string) {
    BluetoothSerial.write(data)
  },

  listDevices: function() {
    BluetoothSerial.list()
  },

  connectFailed: function() {
    alert("Connection to device failed!")
  },

  onError: function(errorMsg:string) {
    console.log('Error: ' + errorMsg + '\n')
  },

  test1: function() {
    return ( <p>This is a test</p> )
  }
};

interface ContainerProps {
    title: string;
  }
  
  const BluetoothButton: React.FC<ContainerProps> = ({ title }) => {
    return (
      <div className="container">
        <strong>{title}</strong>
            <p>
                <IonButton color="primary" size= "large" onClick= {bluetoothComponent.init}>Scan for devices</IonButton>
            </p>
            <div id="device">{bluetoothComponent.test1()}</div>
      </div>
    );
  };
  
  export default BluetoothButton;
